import { Component, DestroyRef, ElementRef, inject, OnInit, ViewChild } from '@angular/core';
import { MatIcon } from '@angular/material/icon';
import { TranslateModule } from '@ngx-translate/core';
import { MatRipple } from '@angular/material/core';
import { NgStyle, TitleCasePipe } from '@angular/common';
import { ActivatedRoute, RouterLink, RouterLinkActive } from '@angular/router';
import {
  CdkDrag,
  CdkDragDrop,
  CdkDragPlaceholder,
  CdkDropList,
  moveItemInArray,
  transferArrayItem
} from '@angular/cdk/drag-drop';
import { combineLatest, of, switchMap, tap } from 'rxjs';
import { ScrollbarDirective } from '../../../config/directives/scrollbar.directive';
import { TaskCardComponent } from '../tasks-card/task-card.component';
import { TasksService } from '../../../core/services/tasks.service';
import { takeUntilDestroyed } from '@angular/core/rxjs-interop';
import { MatDialog } from '@angular/material/dialog';
import { ObjectiveManageModalComponent } from '../objective-manage-modal/objective-manage-modal.component';

@Component({
  selector: 'tasks-board',
  standalone: true,
  imports: [
    MatIcon,
    TranslateModule,
    MatRipple,
    TitleCasePipe,
    RouterLink,
    RouterLinkActive,
    CdkDropList,
    CdkDrag,
    CdkDragPlaceholder,
    NgStyle,
    ScrollbarDirective,
    TaskCardComponent
  ],
  templateUrl: './tasks-board.component.html',
  host: {
    'style': 'display: block; height: 100%'
  },
})

export class TasksBoardComponent implements OnInit {
  @ViewChild('tasksGrid', { static: true }) tasksGrid: ElementRef;

  tasks = {
    inProgress: [],
    inReview: [],
    done: []
  };

  private _tasksService = inject(TasksService);
  private _destroyRef = inject(DestroyRef);
  private _matDialog = inject(MatDialog);
  private _activatedRoute = inject(ActivatedRoute);

  ngOnInit() {
    this._tasksService.refreshBoard$
      .subscribe(_ => {
        this.getTasksList();
      })
    this.getTasksList();
  }

  drop(event: CdkDragDrop<number[]>) {
    if (event.previousContainer === event.container) {
      moveItemInArray(event.container.data, event.previousIndex, event.currentIndex);
    } else {
      transferArrayItem(
        event.previousContainer.data,
        event.container.data,
        event.previousIndex,
        event.currentIndex
      );
    }

    const previousStatus = +event.previousContainer?.id
    const status = +event.container?.id

    if (previousStatus !== status) {
      this._tasksService.updateObjectiveStatus((event.item?.data as any)?.id, status)
        .pipe(takeUntilDestroyed(this._destroyRef))
        .subscribe({
          next: () => {

          }
        })
    }
  }

  openDetailsDialog(item: any): void {
      this._matDialog.open(ObjectiveManageModalComponent, {
        width: '700px',
        maxWidth: '100%',
        data: item
      })
  }

  getTasksList(): void {
    combineLatest([
      this._tasksService.getObjectivesList({ status: 1 }),
      this._tasksService.getObjectivesList({ status: 2 }),
      this._tasksService.getObjectivesList({ status: 3 })
    ]).pipe(
      switchMap((params: any[]) => of({
        inProgress: params[0],
        inReview: params[1],
        done: params[2],
      })),
      tap(({inProgress, inReview, done}) => {
        this.tasks.inProgress = inProgress?.data || [];
        this.tasks.inReview = inReview?.data || [];
        this.tasks.done = done?.data || [];
        this.openModalById()
      })
    ).subscribe();
  }

  openModalById(): void {
    this._activatedRoute.queryParams
      .pipe(takeUntilDestroyed(this._destroyRef))
      .subscribe((data: { objectiveId: string }) => {
        if (!data?.objectiveId) return

        const item = [...this.tasks.inProgress, ...this.tasks.inReview, ...this.tasks.done]
          .find(i => i.id === data?.objectiveId)

        if (!item) return;

        this.openDetailsDialog(item);
      })
  }
}
<div class="grid grid-cols-4 gap-6 grow h-full" #tasksGrid>
  <div class="flex flex-col gap-6">
    <div
      class="flex items-center justify-between h-14 px-4 rounded-[8px] bg-white border-t-2 border-t-[#2E90FA] text-[#101828] font-medium">
      <span>{{ 'in.progress'| translate }}</span>

      <span class="grid place-items-center h-6 px-3 border border-[#E4E7EC] rounded-full">
        {{ tasks?.inProgress?.length }}
      </span>
    </div>

    <div class="relative grow">
      <div
        class="flex flex-col gap-3 absolute inset-0 overflow-auto scrollbar-thin scrollbar-thumb-gray-100 scrollbar-track-white"
        cdkDropList
        #inProgressList="cdkDropList"
        [cdkDropListData]="tasks?.inProgress"
        [id]="'1'"
        [cdkDropListConnectedTo]="[inReviewList, doneList]"
        (cdkDropListDropped)="drop($event)">
        @for (item of tasks?.inProgress; track item) {
          <div cdkDrag [cdkDragData]="item">
            <task-card (click)="openDetailsDialog(item)" [task]="item"/>
            <div class="h-48 shrink-0 bg-[#F2F4F7] rounded-[12px]" *cdkDragPlaceholder></div>
          </div>
        }
      </div>
    </div>
  </div>

  <div class="flex flex-col gap-6">
    <div
      class="flex items-center justify-between h-14 px-4 rounded-[8px] bg-white border-t-2 border-t-[#FDB022] text-[#101828] font-medium">
      <span>{{ 'in.review'| translate }}</span>

      <span class="grid place-items-center h-6 px-3 border border-[#E4E7EC] rounded-full">
            {{ tasks?.inReview?.length }}
          </span>
    </div>

    <div class="relative grow">
      <div
        class="flex flex-col gap-3 absolute inset-0 overflow-auto scrollbar-thin scrollbar-thumb-gray-100 scrollbar-track-white"
        cdkDropList
        #inReviewList="cdkDropList"
        [cdkDropListData]="tasks?.inReview"
        [id]="'2'"
        [cdkDropListConnectedTo]="[inProgressList, doneList]"
        (cdkDropListDropped)="drop($event)">
        @for (item of tasks?.inReview; track item) {
          <div cdkDrag [cdkDragData]="item">
            <task-card
              (click)="openDetailsDialog(item)"
              [task]="item"/>
            <div class="h-48 shrink-0 bg-[#F2F4F7] rounded-[12px]" *cdkDragPlaceholder></div>
          </div>
        }
      </div>
    </div>
  </div>

  <div class="flex flex-col gap-6">
    <div
      class="flex items-center justify-between h-14 px-4 rounded-[8px] bg-white border-t-2 border-t-[#12B76A] text-[#101828] font-medium">
      <span>{{ 'done'| translate }}</span>

      <span class="grid place-items-center h-6 px-3 border border-[#E4E7EC] rounded-full">
            {{ tasks?.done?.length }}
          </span>
    </div>

    <div class="relative grow">
      <div
        class="flex flex-col gap-3 absolute inset-0 overflow-auto scrollbar-thin scrollbar-thumb-gray-100 scrollbar-track-white"
        cdkDropList
        #doneList="cdkDropList"
        [cdkDropListData]="tasks?.done"
        [id]="'3'"
        [cdkDropListConnectedTo]="[inProgressList, inReviewList]"
        (cdkDropListDropped)="drop($event)">
        @for (item of tasks?.done; track item) {
          <div cdkDrag [cdkDragData]="item">
            <task-card (click)="openDetailsDialog(item)" [task]="item"/>
            <div class="h-48 shrink-0 bg-[#F2F4F7] rounded-[12px]" *cdkDragPlaceholder></div>
          </div>
        }
      </div>
    </div>
  </div>
</div>
