import { Component, inject, Inject } from '@angular/core';
import { MAT_SNACK_BAR_DATA, MatSnackBarRef } from '@angular/material/snack-bar';
import { NgClass, NgStyle } from '@angular/common';
import { MatIcon } from '@angular/material/icon';
import { TranslateModule } from '@ngx-translate/core';
import { MatRipple } from '@angular/material/core';

@Component({
  selector: 'toaster',
  templateUrl: './toaster.component.html',
  styleUrls: [ './toaster.component.scss' ],
  imports: [
    NgStyle,
    NgClass,
    MatIcon,
    TranslateModule,
    MatRipple
  ],
  standalone: true
})

export class ToasterComponent {
  private _snackBarRef: MatSnackBarRef<ToasterComponent> = inject(MatSnackBarRef<ToasterComponent>)
  @Inject(MAT_SNACK_BAR_DATA) public data: {
    type: 'success' | 'warning' | 'info' | 'error';
    title: string;
    message: string;
    dismissible: boolean;
    duration: number;
  } = inject(MAT_SNACK_BAR_DATA);

  dismissSnackbar(): void {
    this._snackBarRef.dismiss()
  }
}
<div class="toaster">
  <div class="toaster-inner">
    <div [class]="'toaster-decoration ' + data.type"></div>

    <mat-icon class="toaster-icon" [svgIcon]="'icon:toaster-' + data.type"/>

    <div class="toaster-body">
      @if (data.title) {
        <div class="toaster-title">{{ data.title| translate }}</div>
      }

      <p class="toaster-text">{{ data.message| translate }}</p>
    </div>

    <div
      class="grid place-items-center w-8 h-8 rounded-full hover:bg-[#F2F4F7]"
      mat-ripple
      (click)="dismissSnackbar()"
    >
      <mat-icon [svgIcon]="'icon:x'"/>
    </div>
  </div>

  <div
    [class]="'toaster-progress ' + data.type +
        (data.dismissible ? ' progress-' + data.duration / 1000 : '')
    "
  ></div>
</div>
