import { Component, DestroyRef, inject, OnInit } from '@angular/core';
import { MatIcon } from '@angular/material/icon';
import { MatOption, MatRipple } from '@angular/material/core';
import { RouterOutlet } from '@angular/router';
import { TranslateModule } from '@ngx-translate/core';
import { MembersCardComponent } from './members-card/members-card.component';
import { ScrollbarDirective } from '../../config/directives/scrollbar.directive';
import { PaginationComponent } from '../../core/components/pagination/pagination.component';
import { MatSelect } from '@angular/material/select';
import { FormsModule } from '@angular/forms';
import { NgOptimizedImage } from '@angular/common';
import { MembersTableComponent } from './members-table/members-table.component';
import { MembersService } from '../../core/services/members.service';
import { takeUntilDestroyed } from '@angular/core/rxjs-interop';
import { MemberModel } from '../../core/models/member.model';

@Component({
  selector: 'members',
  standalone: true,
  imports: [
    MatIcon,
    MatRipple,
    RouterOutlet,
    TranslateModule,
    MembersCardComponent,
    ScrollbarDirective,
    PaginationComponent,
    MatSelect,
    MatOption,
    FormsModule,
    NgOptimizedImage,
    MembersTableComponent
  ],
  templateUrl: './members.component.html'
})

export class MembersComponent implements OnInit {
  viewType: 'grid' | 'list' = 'grid';

  params = {
    limit: 15,
    page: 1,
    total: 0,
    department_ids: []
  };

  members: MemberModel[] = [];
  departments: any[] = [];

  private _membersService = inject(MembersService);
  private _destroyRef = inject(DestroyRef);

  ngOnInit() {
    this.getMembersList();
    this.getDepartments();
  }

  getMembersList(): void {
    this._membersService.getMembersList(this.params)
      .pipe(takeUntilDestroyed(this._destroyRef))
      .subscribe({
        next: res => {
          this.members = res?.data || [];
          this.params.total = res?.total || 0;
        }
      });
  }

  changePage(page: number): void {
    this.params.page = page;
    this.getMembersList();
  }

  getDepartments(): void {
    this._membersService.getDepartmentsList()
      .pipe(takeUntilDestroyed(this._destroyRef))
      .subscribe(res => {
        this.departments = res?.data || [];
      })
  }

  onLimitChange(): void {
    this.params.page = 1;
    this.getMembersList();
  }

  search(): void {
    this.params.page = 1;
    this.getMembersList();
  }
}
<div class="flex flex-col h-full">
  <div class="flex items-center justify-between px-6 pb-6 bg-white border-b border-b-[#F2F4F7]">
    <div class="flex items-center gap-3">
      <div class="relative w-[300px] shrink-0">
        <input
          class="w-full h-12 pl-[65px] pr-[26px] rounded-[10px] bg-[#F8F8F8] border-0 focus:outline-0 placeholder:text-[#667085] text-[#101828]"
          type="search"
          [placeholder]="'search.for.members'| translate"
        >

        <mat-icon class="absolute left-[26px] top-[11px]" [svgIcon]="'icon:search'"/>
      </div>

      <mat-select
        [(ngModel)]="params.department_ids"
        [placeholder]="'departments'| translate"
        class="outlined-select departments"
        [multiple]="true"
        (selectionChange)="search()"
      >
        @for (department of departments; track department) {
          <mat-option [value]="department?.id">{{ department?.department_name }}</mat-option>
        }
      </mat-select>
    </div>

    <div class="flex items-center gap-6">
      <div
        class="flex items-center justify-center h-12 px-6 gap-3 border border-[#F2F4F7] rounded-[10px] cursor-pointer"
        mat-ripple
        (click)="viewType === 'grid' ? viewType = 'list' : viewType = 'grid'"
      >
        @if (viewType === 'grid') {
          <mat-icon class="text-[#667085]" [svgIcon]="'icon:layout-card'"/>
        } @else {
          <mat-icon class="text-[#667085]" [svgIcon]="'icon:layout-list'"/>
        }

        <span class="font-semibold text-[#101828]">
          {{ 'layout'| translate }}: {{ (viewType === 'grid' ? 'card' : 'list')| translate }}
        </span>
      </div>

      <div
        class="flex items-center justify-center h-12 px-6 gap-3 border border-[#F2F4F7] rounded-[10px] cursor-pointer"
        mat-ripple>
        <mat-icon class="text-[#667085]" [svgIcon]="'icon:filter'"/>
        <span class="font-semibold text-[#101828]">{{ 'filter'| translate }}: {{ 'deadline'| translate }}</span>
      </div>
    </div>
  </div>

  <div class="flex flex-col gap-5 grow p-6">
    <div class="relative grow">
      @if (viewType === 'grid') {
        <div
          class="grid lg:grid-cols-3 xl:grid-cols-4 2xl:grid-cols-5 place-content-baseline gap-5 absolute inset-0 overflow-auto"
        >
          @for (member of members; track member) {
            <members-card
              [member]="member"
            />
          }
        </div>
      }
      @if (viewType === 'list') {
        <div class="absolute inset-0 overflow-auto">
          <members-table [members]="members"/>
        </div>
      }
    </div>

    <div class="flex items-center justify-between h-16 px-4 bg-white rounded-[12px]">
      <pagination
        [collectionSize]="params.total"
        [pageSize]="params.limit"
        (onPageChange)="changePage($event)"
      />

      <div class="flex items-center gap-3">
        <div class="text-[#101828] shrink-0">
          {{ (params.page - 1) * params.limit + 1 }}-{{ params.page * params.limit >= params.total ? params.total : params.page * params.limit }}/{{ params.total }}
        </div>

        <mat-select
          [(ngModel)]="params.limit"
          class="outlined-select"
          [hideSingleSelectionIndicator]="true"
          (selectionChange)="onLimitChange()"
        >
          <mat-option [value]="15">{{ 'show'| translate }} 15</mat-option>
          <mat-option [value]="20">{{ 'show'| translate }} 20</mat-option>
          <mat-option [value]="25">{{ 'show'| translate }} 25</mat-option>
        </mat-select>
      </div>
    </div>
  </div>
</div>
